-- POPULANDO A DIMENSAO SALA
INSERT INTO stg."dim_sala"(num_sala, capacidade, cinema, local, cidade, estado, regiao, pais)
SELECT 
	sala.codsala,
	sala.capacidade,
	cinema.nome nomecinema,
	local.nomelocal,
	cidade.nome nomecidade,
	estado.nome nomeestado,
	regiao.nome nomeregiao,
	pais.nome nomepais
FROM stg.stg_sala sala 
	INNER JOIN stg.stg_cinema cinema ON sala.codcinema = cinema.codcinema
	INNER JOIN stg.stg_local local ON sala.codlocal = local.codlocal
	INNER JOIN stg.stg_cidade cidade ON local.codcidade = cidade.codcidade
	INNER JOIN stg.stg_estado estado ON local.codestado = estado.codestado
	INNER JOIN stg.stg_regiao regiao ON local.codregiao = regiao.codregiao
	INNER JOIN stg.stg_pais pais ON local.codpais = pais.codpais;


-- POPULANDO A DIMENSAO FILME
