SELECT 
	nome, 
	codfilme,
	genero
FROM stg.stg_filme;


-- POPULANDO A DIMENSAO PARTIC	
INSERT INTO stg."dim_participa"(nome_artista, codfilme, genero, nome_filme, papel, rank)
SELECT 
	artista.nome, 
	filme.codfilme,
	filme.genero,
	filme.nome,
	participa.papel, 
	participa.rank
FROM stg.stg_participa participa
INNER JOIN stg.stg_artista artista ON participa.codartista = artista.codartista
INNER JOIN stg.stg_filme filme ON participa.codfilme = filme.codfilme;


