-- CRIANDO E POPULANDO A FATO EXIBIÇÃO
INSERT INTO stg."fato_exibicao"(cod_particip, cod_sala, cod_tempo, data, publico, arrecad)
SELECT
	d_participa.id_particip, 
	d_sala.id_sala,
	d_tempo.id_tempo,
	d_tempo.data,
--	date(substr(exibe.data, 7, 4)|| '-' || substr(exibe.data, 4, 2)|| '-' || substr(exibe.data, 1, 2)||' 18:40:34') data,
	exibe.publico,
	exibe.arrecad
FROM stg."stg_exibe" exibe
INNER JOIN stg."dim_sala" d_sala ON d_sala.num_sala = exibe.codsala
INNER JOIN stg."dim_participa" d_participa ON d_participa.codfilme = exibe.codfilme
INNER JOIN stg."dim_tempo" d_tempo ON d_tempo.data = date(substr(exibe.data, 7, 4)|| '-' || substr(exibe.data, 4, 2)|| '-' || substr(exibe.data, 1, 2)||' 18:40:34')
